            //Create and assign dataview
            var consoleContent = ContentView.build().setDataDisplay(new ConsoleView(true));
            consoleView.Content = consoleContent.getContentView();//.setDataDisplay(new ConsoleView(true)); //True to assign the console output to get redirected!
            //Resize the tab control !
            PApp_ViewTabber.Width = consoleContent.Width;
