            userView.Content = ContentView.build().
                setDataDisplay(new UserDataView().
                    getDisplay()).
                        getContentView();

            //Add Initial Views
            PApp_ViewTabber.Items.Add(consoleView);
            PApp_ViewTabber.Items.Add(userView);







            //Make Context Menu
            ContextMenu tabBarContextMenu = new ContextMenu();

            //Create Menu Items, define click events, then add them to CM

