            PApp_ViewTabber.ContextMenu = tabBarContextMenu;

            TEST_Button.Click += async(sender, a) =>
            {
                
                IDictionary<string, string> dic = new Dictionary<string, string>();
                dic["data"] =
                    "";
                dic["uid"] = "USRADB40C3A4484D392271B8B83F76FBA0E6051C438BEC7BDB6D5EB5DB8A72EDE1F";
                dic["mime"] = "image/jpeg";
                dic["name"] = "This is a test pic!";

                string json = JsonConvert.SerializeObject(dic);
                //Encode Json to send to 
                string encoded = Convert.ToBase64String(Encoding.ASCII.GetBytes(json));

                Debug.WriteLine(encoded);
                

                var postUrl  = "api/pics/post/create";


                HttpClient client = new HttpClient();
                client.BaseAddress = new UriBuilder("http://localhost:59107/").Uri;

                //Console.WriteLine(client.BaseAddress+postUrl);



                var res = await client.PostAsync(postUrl, new StringContent(encoded));
                Console.WriteLine("Posted! \n"+ res.RequestMessage);


